import {useState} from "react"

function Square({position, value, onSquareClick}) {
  return (
    <button
      className="square"
      onClick={onSquareClick}
    >
      {value}
    </button>
  );
}

export default function Board() {
  const [squares, setSequares] = useState(Array(9).fill("Q"));

  function handleClick(pos) {
    const new_squares = squares.slice();
    {/*slice creates a shallow copy - non-primitive values are shared.
       since our array only contains primitive strs, this is sufficient.
       For deep copying, another library will be needed.*/}
    new_squares[pos] = "X";
    setSequares(new_squares);
  }
  return (
    <>
      <div className="board-row">
        <Square position="0" value={squares[0]} onSquareClick={handleClick(12)}/>
        <Square value="2"/>
        <Square value="3"/>
        {/*positional arguments only for React*/}
      </div>

      <div className="board-row">
        <Square value="4"/>
        <Square value="5"/>
        <Square value="6"/>
      </div>      

      <div className="board-row">
        <Square value="7"/>
        <Square value="8"/>
        <Square value="9"/>
      </div>
    </>
  );
}

function compB() {
  return (
    <Square value="111"/>
  )
}

export {compB};
